INSERT INTO CATEGORIA_NAVE VALUES(1, 'Vehiculos lanzadera', 'Se trata de un cohete autopropulsado que sirve para lanzar una carga útil al espacio, normalmente un satélite artificial, una sonda o una nave tripulada.');
INSERT INTO CATEGORIA_NAVE VALUES(2, 'Naves espaciales no tripuladas', 'Las naves espaciales de suministro no tripuladas son un tipo especial de nave espacial robótica que opera de forma autónoma sin tripulación humana, diseñada para respaldar operaciones en una estación espacial.');
INSERT INTO CATEGORIA_NAVE VALUES(3, 'Naves espaciales tripuladas', 'Su propósito consiste en mandar seres humanos al espacio para tareas de reparación, mantenimiento o investigación, en misiones donde se precisa de la destreza y de la toma de decisiones de personas, en detrimento de las máquinas.');